mapper.select:
SELECT id, name, role, create_time, update_time FROM user
AND name LIKE '%${user.name}%'
AND role = #{user.role}
ORDER BY create_time DESC LIMIT ${page.startNum}, ${page.size};

SELECT COUNT(*) FROM meta_user
AND name LIKE '%${user.name}%'
AND role = #{user.role}

SELECT id, `name` as name, role, create_time, update_time FROM meta_user mu
AND name LIKE '%${user.name}%'
AND role = #{user.role}

SELECT
meta_user.name
FROM
meta_user_group_rela
INNER JOIN meta_user  ON meta_user_group_rela.user_id = meta_user.id
INNER JOIN meta_task_group_rela  ON meta_task_group_rela.group_id = meta_user_group_rela.group_id
WHERE
meta_user.role = #{role}
AND meta_task_group_rela.task_type_id = #{taskTypeId}
AND meta_user_group_rela.sub_group_id = #{subGroupId}
